
public class Uso_arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 
		 * La formula de abajo es una declaracion simple
		 * 
		int [] mi_matriz= new int [5];
		
		mi_matriz[0]=5;
		mi_matriz[1]=38;
		mi_matriz[2]=-15;
		mi_matriz[3]=92;
		mi_matriz[4]=71;
		
		*/
		
		
		int[] mi_matriz= {5, 38, -15, 
				14, 152, 12, 124, 92, 71}; //esta declaracion es en la misma linea, nos vale para pocos elementos
		
		
		
		for(int i=0;i<5;i++) {
			
			System.out.println(mi_matriz[i]);
			
		}
		
		
	}

}
